function interpolateColor(color1, color2, factor) {
    const r1 = parseInt(color1.slice(1, 3), 16);
    const g1 = parseInt(color1.slice(3, 5), 16);
    const b1 = parseInt(color1.slice(5, 7), 16);
    
    const r2 = parseInt(color2.slice(1, 3), 16);
    const g2 = parseInt(color2.slice(3, 5), 16);
    const b2 = parseInt(color2.slice(5, 7), 16);
    
    const r = Math.round(r1 + factor * (r2 - r1));
    const g = Math.round(g1 + factor * (g2 - g1));
    const b = Math.round(b1 + factor * (b2 - b1));
    
    return `#${(1 << 24 | r << 16 | g << 8 | b).toString(16).slice(1).toLowerCase()}`;
}
const GRID_SIZE = 40;
const colors = [...Array(GRID_SIZE)].map(x => [...Array(GRID_SIZE)])

const canvas = document.getElementById("canvas");
/**
 * @type CanvasRenderingContext2D
 */
const ctx = canvas.getContext("2d");

const {sqrt, log, floor, ceil} = Math;

let f = x => ceil(sqrt(x / 1.1));

let rarity_colors = [
    '#000',
    '#7eef6d', // c
    '#bfeb65', // C
    '#ffe65d', // u
    '#caba85', // U
    '#4d52e3', // r
    '#6a39e1', // R
    '#861fde', // e
    '#b21f7f', // E
    '#de1f1f', // l
    '#58a3a5', // L
    '#1fdbde', // m
    '#8f83aa', // M
    '#ff2b75', // x
    '#f70fb6'  // X
    // "#000", // 0
    // "#7eef6d", // c
    // "", // C
    // "#ffe65d", // u
    // "", // U
    // "#4d52e3", // r
    // "", // R 
    // "#861fde", // e
    // "", // E
    // "#de1f1f", // l
    // "", // L
    // "#1fdbde", // m
    // "", // M
    // "ff2b75", // x
    // "#ff2b75" // X
]

const rarity_names = "_cCuUrReElLmMxX".split("");

function loop()
{
    ctx.canvas.width = innerWidth;
    ctx.canvas.height = innerHeight;

    for (let x = 0; x < GRID_SIZE; x++)
        for (let y = 0; y < GRID_SIZE; y++)
        {
            if (ds[x][y] == null)
                colors[x][y] = 0;
            else
                colors[x][y] = f(ds[x][y]);
        }
    
    const s = Math.min(innerWidth, innerHeight) / GRID_SIZE;
    ctx.scale(s, s);
    ctx.textBaseline = "middle";
    ctx.textAlign = "center";
    ctx.font = "1px";

    for (let x = 0; x < GRID_SIZE; x++)
        for (let y = 0; y < GRID_SIZE; y++)
        {
            ctx.fillStyle = rarity_colors[colors[x][y]]
            ctx.fillRect(x, y, x + 1, y + 1);
            ctx.save();
            ctx.scale(0.1, 0.1);
            ctx.translate(5, 5);
            ctx.strokeText(rarity_names[colors[x][y]], x * 10, y * 10);
            ctx.fillText  (rarity_names[colors[x][y]], x * 10, y * 10);
            ctx.restore();
        }

    requestAnimationFrame(loop);
}
loop();
